    $('li').click(function() {
        $('li').removeClass('active');
        $(this).addClass('active');
    });

    function updatePos(factor_number, code) {
        $.ajax({
            url: 'server.php',
            data: {
                store: 'ok',
                factor_id: factor_number,
                pos: code,
            },
            type: 'GET',
            success: function(result) {
                $('#pos' + factor_number).text(result);
            }
        });
    }
